package com.saxion.proj.tfms.routing.service.interfaces;

import com.saxion.proj.tfms.routing.dto.VrpRequestDto;
import com.saxion.proj.tfms.routing.service.DistanceMatrixService;

import java.util.List;
import java.util.Map;

/**
 * Interface for location mapping and management
 * Follows Single Responsibility Principle - focused on location handling
 */
public interface LocationMappingService {

    /**
     * Build location mapping from VRP request
     * 
     * @param request VRP request containing location data
     * @return LocationMapping with indexed locations
     */
    LocationMapping buildLocationMapping(VrpRequestDto request);

    /**
     * Location mapping data structure
     */
    class LocationMapping {
        private final List<DistanceMatrixService.Location> locations;
        private final Map<String, Integer> locationIndices;
        private final int depotIndex;

        public LocationMapping(List<DistanceMatrixService.Location> locations,
                             Map<String, Integer> locationIndices,
                             int depotIndex) {
            this.locations = locations;
            this.locationIndices = locationIndices;
            this.depotIndex = depotIndex;
        }

        public List<DistanceMatrixService.Location> getLocations() { return locations; }
        public Map<String, Integer> getLocationIndices() { return locationIndices; }
        public int getDepotIndex() { return depotIndex; }
    }
}

package com.saxion.proj.tfms.routing.service.interfaces;

import com.saxion.proj.tfms.routing.dto.VrpRequestDto;

/**
 * Interface for VRP request validation
 * Follows Single Responsibility Principle - focused only on validation
 */
public interface VrpRequestValidator {

    /**
     * Validate VRP request for common issues and constraints
     * 
     * @param request VRP request to validate
     * @throws IllegalArgumentException if validation fails
     */
    void validateRequest(VrpRequestDto request);

    /**
     * Check if request is valid without throwing exceptions
     * 
     * @param request VRP request to check
     * @return ValidationResult containing status and error messages
     */
    ValidationResult isValid(VrpRequestDto request);

    /**
     * Validation result container
     */
    class ValidationResult {
        private final boolean valid;
        private final String errorMessage;

        public ValidationResult(boolean valid, String errorMessage) {
            this.valid = valid;
            this.errorMessage = errorMessage;
        }

        public boolean isValid() { return valid; }
        public String getErrorMessage() { return errorMessage; }

        public static ValidationResult success() {
            return new ValidationResult(true, null);
        }

        public static ValidationResult failure(String message) {
            return new ValidationResult(false, message);
        }
    }
}
